CREATE TABLE IF NOT EXISTS users (user_id INTEGER PRIMARY KEY AUTO_INCREMENT, serial VARCHAR(32) UNIQUE NOT NULL, username VARCHAR(64) UNIQUE NOT NULL, credit FLOAT, is_admin BOOLEAN);
CREATE TABLE IF NOT EXISTS drinks (drink_id INTEGER PRIMARY KEY AUTO_INCREMENT, drink_name VARCHAR(64) UNIQUE NOT NULL, price FLOAT, description VARCHAR(256));
CREATE TABLE IF NOT EXISTS credit_log (user_id INTEGER, drink_id INTEGER, amount INTEGER, price_payed FLOAT);